import React from 'react'
import axios from 'axios'
import { connect } from 'react-redux'
import { useHistory, Link } from "react-router-dom"

class ShowArticle extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      article: []
    };
  }

  getUsersArticle() {
    const { id } = this.props.match.params
    const article = "";
    axios.get(`/api/v1/articles/${id}`,{})
    .then(res => {
      const data = res.data
      if(data.success){
        article = data.articles
      }else{
        article = ''
      }
      this.setState({article})
    }).catch(error => {
      console.log(error)
    })
  }

  componentDidMount(){
    this.getUsersArticle()
  }
  render(){
    return (
      <div className="content-page">
        <div className="content">
          <div className="container-fluid">
            <div className="row">
              <div className="col-12">
                <div className="page-title-box">
                  <h4 className="page-title">Details</h4>
                </div>
              </div>
            </div>
            {this.state.article}
            <div className="row">
              <div className="col-xl-3">
                <div className="card">
                  <div className="card-body">
                    <div className="d-flex align-items-center">
                      <img src="/assets/users/def.png" alt="team-member" className="avatar-lg d-block img-thumbnail rounded-circle"/>
                      <div className="w-100 ms-3">
                        <h4 className="mt-0">Daniel Syme</h4>
                        <p className="mb-0">Founder</p>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
              <div className="col-xl-6">
                <div className="card">
                  <div className="card-body">
                    <div className="border border-light p-2 mb-3">
                      <div className="d-flex align-items-start">
                        <div className="w-100">
                          <h5 className="m-0">Jeremy Tomlinson</h5>
                          <p className="text-muted"><small>about 2 minuts ago</small></p>
                        </div>
                      </div>
                      <img src="/assets/users/def.png" alt="post-img" className="rounded" height="60"/>
                      <p>Story based around the idea of time lapse, animation to post soon!</p>
                      <div className="mt-2">
                        <a href="#" className="btn btn-sm btn-link text-muted"><i className="mdi mdi-reply"></i> Back</a>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    )
  }
}
export default ShowArticle
