import React from 'react'
import axios from 'axios'
import { connect } from 'react-redux'
import { useHistory, Link } from "react-router-dom"

class ArticlesList extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      list: [],
      page: []
    };
  }

  handleClick(e) {
    console.log('-----')
    console.log(e.target.id)
    console.log('-----')
    e.preventDefault()
  }

  getUsersArticles(page_no=1) {
    const list = []
    const page = []
    axios.get(`/api/v1/articles?page_no=${page_no}`,{})
    .then(res => {
      const data       = res.data
      if(data.success){
        const pagination = data.pagination
        data.articles.forEach( (article, i) => {
          list.push( 
            <div key={article.id} className="border border-light p-2 mb-3">
              <div className="d-flex align-items-start">
                <img className="me-2 avatar-lg rounded-circle" src={article.profile_pc} alt="image"/>
                <div className="w-100">
                  <h5 className="m-0">{article.user_nm}</h5>
                  <p className="text-muted"><small>{article.publish_date}</small></p>
                </div>
              </div>
              <h2>{article.title}</h2>
              <h4>{article.content}</h4>
              <div className="mt-2">
                <Link className="btn btn-sm btn-link text-muted" to={`/articles/${article.id}`}>
                  <i className="mdi mdi-reply"></i> Details
                </Link>
                <a className="btn btn-sm btn-link text-danger">
                  <i className="mdi mdi-heart"></i> View ({article.view_count})
                </a>
              </div>
              <div className="mt-2"></div>
            </div>
          )
        })
        if(pagination.next){
          page.push(
            <a id={pagination.next} key={pagination.next} className="btn btn-primary" href="#" onClick={this.handleClick.bind(this)}>
              <i className="mdi mdi-spin me-1"></i> Load More
            </a>
          )
        }
        this.setState({list})
        this.setState({page})
      }
    }).catch(error => {
      console.log(error)
    })
  }

  componentDidMount(){
    this.getUsersArticles()
  }

  render(){
    const { isAuthenticated } = this.props
    return (
      <div className="content-page">
        <div className="content">
          <div className="container-fluid">
            <div className="row">
              <div className="col-12">
                <div className="page-title-box">
                  <h4 className="page-title">Articles</h4>
                </div>
              </div>
            </div>
            <div className="row">
              <div className="col-xl-9">
                <div className="card">
                  <div className="card-body">
                    {this.state.list}
                    <div className="text-center">
                      {this.state.page}
                    </div>
                  </div>
                </div>
              </div>
              <div className="col-xl-3">
              </div>
            </div>
          </div>
        </div>
        {isAuthenticated &&
          <footer className="footer">
            <div className="container-fluid">
              <div className="row">
                <div className="col-md-6">
                  <script>document.write(new Date().getFullYear())</script>2021 © Code
                </div>
                <div className="col-md-3"></div>
              </div>
            </div>
          </footer>
        }
      </div>
    )
  }
}
function mapStateToProps(state) {
  const { auth } = state
  const { isAuthenticated } = auth
  return { isAuthenticated }
}
export default connect(mapStateToProps)(ArticlesList)
