import React from 'react'
import { Link } from 'react-router-dom'
import { connect } from 'react-redux'
import { List, ListItem, ListSubHeader } from 'react-toolbox/lib/list'
import { fetchArticles } from '../modules/ArticleList'


class AllArticles extends React.Component {

  componentDidMount() {
    this.props.dispatch(fetchArticles())
  }

  renderArticles() {
    const { articles } = this.props
    const articleList = []
    // articles.forEach((note, i) => {
    //   articleList.push(
    //     // <ListItem key={i} itemContent={<div>
    //     //   <Link to={`/articles/${article.id}`}>{article.title}</Link> <a data-id={article.id} data-title={article.title} href="#" >Delete</a>
    //     // </div>} />
    //     <div>shshs</div>
    //   )
    // })
    return (
      <List selectable ripple>
        <ListSubHeader caption='Article List' />
        {articleList}
      </List>
    )
  }

  render() {
    const { loading } = this.props
    if (loading) {
      return (
        <div>
          <h2>Article List</h2>
          <p>Loading...</p>
        </div>
      )
    }
    return (
      <div>
        {this.renderArticles()}
      </div>
    )
  }
}

function mapStateToProps(state) {
  // const articleListState = state.articles
  // const { loading, articles } = articleListState
  // return { loading, articles }
  return {}
}

export default connect(mapStateToProps)(AllArticles)
