import React from 'react'
import { Link } from 'react-router-dom'
import { connect } from 'react-redux'
import { List, ListItem, ListSubHeader } from 'react-toolbox/lib/list'
import { fetchArticles } from '../modules/ArticleList'


class ArticlesList extends React.Component {

  componentDidMount() {
    this.props.dispatch(fetchArticles())
  }

  handleClick(e) {
    e.preventDefault()
    const { id, title } = e.target.dataset
    if (confirm(`Are you ok to delete "${title}"?`)) {
      // this.props.dispatch(deleteNote(id))
    }
  }

  renderArticles() {
    const { articles } = this.props
    const ArticleList = []
    articles.forEach((article, i) => {
      ArticleList.push(
        <ListItem key={i} itemContent={<div>
          <Link to={`/articles/${article.id}`}>{article.title}</Link> <a data-id={article.id} data-title={article.title} href="#" onClick={this.handleClick.bind(this)}>Delete</a>
        </div>} />
      )
    })
    return (
      <List selectable ripple>
        <ListSubHeader caption='Articles List' />
        {ArticleList}
      </List>
    )
  }

  render() {
    const { loading } = this.props
    if (loading) {
      return (
        <div>
          <h2>Article List</h2>
          <p>Loading...</p>
        </div>
      )
    }
    return (
      <div>
        {this.renderArticles()}
      </div>
    )
  }
}

function mapStateToProps(state) {
  const articleListState = state.article
  const { loading, articles } = articleListState
  return { loading, articles }
}

export default connect(mapStateToProps)(ArticleList)
