import React from 'react'
import axios from 'axios'

class ArticlesList extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      data: {}
    };
  }

  getUsersData() {
    const list = []
    axios.get('/api/v1/articles',{})
    .then(res => {
      const data = res.data
      console.log(data)
      data.articles.forEach((obj, i) => {
      list.push(
        <div>
          <p>{obj.content}</p>
        </div>
      )
    })
      this.setState({list})
    }).catch(error => {
      console.log(error)
    })
  }

  componentDidMount(){
    this.getUsersData()
  }

  render(){
    return (
      <div>{this.state.users}</div>
    )
  }
}


export default ArticlesList
