import React from 'react'

const REQUEST = 'react-devise-sample/articles/REQUEST'
const RECEIVED = 'react-devise-sample/articles/RECEIVED'
const FAILED = 'react-devise-sample/articles/FAILED'

export function fetchArticles() {
  return (dispatch, getState) => {
    dispatch(startAuthentication())
    return axios({
      url: '/api/v1/articles',
      method: 'GET'
    }).then(response => {
      dispatch(receiveArticles(response.data.articles))
    }).catch(error => {
      dispatch(failFetchArticles())
    })
  }
}

function requestArticles() {
  return { type: REQUEST }
}

function receiveArticles(articles) {
  return { type: RECEIVED, articles }
}

function failFetchArticles() {
  return { type: FAILED }
}

export default function reducer(state = initialState, action = {}) {
  switch (action.type) {
    case REQUEST:
      return Object.assign(
        {},
        state,
        {
          loading: true
        }
      )
    case RECEIVED:
      return {
        loading: false,
        articles: action.articles
      }
    case FAILED:{
      return Object.assign(
        {},
        state,
        {
          loading: false
        }
      )
    }
    default: return state
  }
}

const initialState = {
  loading: false,
  articles: []
}
