import axios from 'axios'

const REQUEST = 'react-devise-sample/auth/REQUEST'
const RECEIVED = 'react-devise-sample/auth/RECEIVED'
const FAILED = 'react-devise-sample/auth/FAILED'
const SIGNOUT = 'react-devise-sample/auth/SIGNOUT'

export function fetchArticles() {
  // return (dispatch, getState) => {
  //   dispatch(requestArticles())
  //   return axios({
  //     url: '/api/v1/articles/list',
  //     method: 'POST'
  //   }).then(response => {
  //     dispatch(receiveArticles(response.data))
  //   }).catch(error => {
  //     dispatch(failFetchArticles())
  //   })
  // }
  return (dispatch, getState) => {
    const { auth } = getState()
    dispatch(requestArticles())
    return axios({
      url: '/api/v1/articles/list',
      headers: {}
    }).then(response => {
      dispatch(requestArticles(response.data))
    }).catch(error => {
      dispatch(failFetchArticles())
      if (error.response && error.response.status === 401) {
        dispatch(expireAuthentication())
      }
    })
  }
}

function requestArticles() {
  return { type: REQUEST }
}

function receiveArticles(data) {
  return { type: RECEIVED, data }
}

function failFetchArticles() {
  return { type: FAILED }
}

function expireAuthentication(){
  console.log('EXP')
}

// Reducer
export default function reducer(state = initialState, action = {}) {
  switch (action.type) {
    case REQUEST:
      return Object.assign(
        {},
        state,
        {
          loading: true
        }
      )
    case RECEIVED:
      return {
        loading: false,
        articles: action.date.articles,
        pagination: action.date.pagination
      }
    case FAILED:{
      return Object.assign(
        {},
        state,
        {
          loading: false
        }
      )
    }
    default: return state
  }
}

const initialState = {
  loading: false,
  isAuthenticated: false,
  client: null,
  accessToken: null,
  uid: null,
  expiry: null
}
